import
  std/unittest,
  stint,
  ../eth/bloom

suite "Simple Bloom Filter tests":
  test "incl proc":
    var f: BloomFilter
    f.incl("hello")
    check "hello" in f
    check "hello1" notin f
    f.incl("hello1")
    check "hello" in f
    check "hello1" in f

  test "int value":
    var f: BloomFilter
    f.incl("value 1")
    f.incl("value 2")
    check f.value == parse("", StUint[2048])
